from . import msg
from .protocol import ProcessChannel

func_id: int

__has_set_out_buffer = False
__has_set_in_buffer = False


def set_out_buffer(buffer: ProcessChannel):
    global __has_set_out_buffer
    if __has_set_out_buffer:
        return
    __has_set_out_buffer = True
    msg.__out_buffer = buffer


def set_in_buffer(buffer: ProcessChannel):
    global __has_set_in_buffer
    if __has_set_in_buffer:
        return
    __has_set_in_buffer = True
    msg.__in_buffer = buffer

